function setCookie(e,t,i="Strict",o=1){const n=new Date;n.setTime(n.getTime()+24*o*60*60*1e3),document.cookie=`${e}=${t||""}; expires=${n.toString()}; SameSite=${i}; secure=true; path=/`}function deleteCookie(e,t="Strict"){document.cookie=`${e}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; SameSite=${t}; secure=true; path=/;`}function getCookie(e){const t=e+"=";for(let e of document.cookie.split(";")){for(;e.startsWith(" ");)e=e.substring(1,e.length);if(e.startsWith(t))return e.substring(t.length,e.length)}return null}