document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".click-to-copy, .click-to-copy-target").forEach((t=>{t.addEventListener("click",(()=>{let e="";if(t.classList.contains("click-to-copy-target")){const c=t.getAttribute("copy-target"),o=document.getElementById(c);e=o?o.textContent:""}else e=t.getAttribute("copy-data");e&&navigator.clipboard.writeText(e).then((()=>{!function(){const t=document.createElement("div");t.className="copy-toast",t.textContent="Copied!",document.body.appendChild(t),setTimeout((()=>{t.remove()}),2e3)}()})).catch((t=>{}))}))}))}));