document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".click-to-copy, .click-to-copy-target").forEach((t=>{t.addEventListener("click",(()=>{if(null!==document.querySelector("div.copy-toast"))return;let e="";if(t.classList.contains("click-to-copy-target")){const o=t.getAttribute("copy-target"),c=document.getElementById(o);e=c?c.textContent:""}else e=t.getAttribute("copy-data");e&&navigator.clipboard.writeText(e).then((()=>{!function(){const t=document.createElement("div");t.className="copy-toast",t.textContent="Copied!",document.body.appendChild(t),setTimeout((()=>{t.remove()}),2500)}()})).catch((t=>{}))}))}))}));