window.addEventListener("load",(()=>{document.querySelectorAll(".click-to-share").forEach((t=>{navigator.userAgent.includes("Mobile")||void 0!==navigator.share?t.addEventListener("click",(()=>{navigator.share({title:"House Party",text:"Join my party on House Party!",url:t.getAttribute("data-party-url")}).catch((t=>{}))})):t.classList.add("hidden")}))}));