window.addEventListener("load",(()=>{navigator.userAgent.includes("Mobile")&&void 0!==navigator.share&&document.querySelectorAll(".click-to-share").forEach((e=>{e.classList.remove("hide"),e.addEventListener("click",(()=>{navigator.share({title:"House Party",text:"Join my party on House Party!",url:e.getAttribute("data-party-url")}).catch((e=>{}))}))}))}));