class ModalHandler{constructor(){this.modal=null}open(e){!this.modal&&e&&(this.modal=document.querySelector(e),this.modal.style.animation="modal-open 0.4s forwards",this.modal.style.display="flex")}close(e){this.modal&&(this.modal.style.animation="modal-close 0.4s forwards",setTimeout((()=>{this.modal.style.display="none",this.modal=null,e&&e()}),400))}}window.addEventListener("load",(()=>{const e=new ModalHandler;document.querySelectorAll(".modal-opener").forEach((l=>{l.addEventListener("click",(()=>{const o=l.getAttribute("modal-target");e.open(`div#${o}`)}))})),document.querySelectorAll(".modal-closer").forEach((l=>{l.addEventListener("click",(()=>{e.close()}))})),document.addEventListener("closeCurrentModal",(l=>{l.detail?e.close(l.detail.callback):e.close()}))}));